pragma solidity ^0.8.20;

/**
 * Defines the contract for the bounty management using vesting option
 * If the vesting contract address is set, tokens will be transferred to the vesting contract,
 * othervise directly to the address
 */

interface IBounty {
    /**
     * @dev Indicates an error when empty token is provided.
     */
    error EmptyToken();

    error NoFundsAvailable(uint256 amount);

    error AgentNotDefined(address agent, address target);

    error NoFundsPermitted(address agent, uint256 amount);

    error NoPermittedFundsAvailable(address agent, uint256 amount);

    error CannotTransferFunds(address agent, address target, uint256 amount);

    function refuel(address agent, uint256 addAmount) external;

    function give(address target, uint256 amount) external;

    function empty(address target) external;

    function clean(address _to) external;

    function vesting() external view returns (address);

    function setVesting(address _vesting) external;

    function balanceOf(address target) external view returns (uint256);

    function isAgent(address target) external view returns (bool);
}
","keccak256":"0x638bb9736cc902629c9704e47fe5a6ccac69c6bd8d80eaa96cad8c7fd301ff51","license":"MIT"},"@bgrusnak/solidity-icoset/contracts/management/IVesting.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "../structures/KPI.sol"; 

interface IVesting {

    /**
     * @dev Indicates an error when empty token is provided.
     */
    error VestingEmptyToken();

    /**
     * @dev Indicates an error when empty airdrop is provided.
     */
    error VestingEmptyAirdrop();
    /**
     * @dev Indicates an error when unathorized person calls airdrop only function
     * @param account Address who calls
     */
    error VestingUnauthorizedAccount(address account);
    /**
     * @dev Indicates an error when no tokens can be redeemed.
     */
    error VestingEmptyRedeem();
    /**
     * @dev Indicates an error when kpi redefines.
     * @param code KPI code
     */
    error VestingKPIAlreadyDefined(bytes32 code);

    /**
     * @dev Indicates an error when kpi is not defined.
     * @param code KPI code
     */
    error VestingKPINotDefined(bytes32 code);
    /**
     * @dev Indicates an error when kpi amount value is incorrect.
     * @param code KPI code
     * @param amount amount provided
     */
    error VestingIncorrectAmount(bytes32 code, uint16 amount);
    /**
     * @dev Indicates an error when kpi weight value is incorrect.
     * @param code KPI code
     * @param amount weight provided
     */
    error VestingIncorrectWeight(bytes32 code, uint16 amount);
    event Redeem(address indexed account, uint256 amount);
    event Distribute(address indexed account, uint256 amount);
    event KPIAdded(
        bytes32 indexed code,
        uint256 indexed time,
        KPITimeStatus indexed timeStatus,
        uint16 weight
    );
    event KPIModified(
        bytes32 indexed code,
        uint256 indexed time,
        KPITimeStatus indexed timeStatus,
        uint16 weight
    );
    event KPIUpdated(bytes32 indexed code, uint16 amount);

    event KPIRemoved(bytes32 indexed code);

    /// @notice Update token address
    /// @param _token New token address.
    function updateToken(address _token) external;

    /// @notice Update token address
    /// @param _airdrop New token address.
    function updateAirdrop(address _airdrop) external;

    /// @notice Distribute the new vesting amount
    /// @param _to Receiver address.
    /// @param _amount Distributed amount.
    function distribute(address _to, uint256 _amount) external;

    /// @notice Get the distributed amount 
    /// @param _to Receiver address.
    function distributed(address _to) external view returns(uint256);

    /// @notice Take the current unlocked amount
    function redeem(address _to) external returns (uint256);

    /// @notice Get the current unlocked amount
    function unlocked(address _to) external view  returns (uint256);

    /// @notice Set the new KPI
    /// @param _code The KPI id code
    /// @param _time timestamp
    /// @param _timeStatus status of the time
    /// @param _weight weight of the KPI parameter in the total KPI
    function addKPI(
        bytes32 _code,
        uint256 _time,
        KPITimeStatus _timeStatus,
        uint16 _weight
    ) external;

    /// @notice Modify the KPI properties
    /// @param _code The KPI id code
    /// @param _time timestamp
    /// @param _timeStatus status of the time
    /// @param _weight weight of the KPI parameter in the total KPI
    function modifyKPI(
        bytes32 _code,
        uint256 _time,
        KPITimeStatus _timeStatus,
        uint16 _weight
    ) external;

    /// @notice Update the  KPI value
    /// @param _code The KPI id code
    /// @param _amount the current value of KPI
    function updateKPI(bytes32 _code, uint16 _amount) external;

    /// @notice Increase the  KPI value
    /// @param _code The KPI id code
    /// @param _amount the added value of KPI
    function increaseKPI(bytes32 _code, uint16 _amount) external;

    /// @notice Remove the KPI from the list
    /// @param _code The KPI id code
    function removeKPI(bytes32 _code) external;

    /// @notice Get the KPI from the list
    /// @param _code The KPI id code
    function getKPI(bytes32 _code) external view returns (KPI memory);
}
","keccak256":"0x7a872f4116681f0fe248f8b2e381e6e7579706f63572492718768a19ae0135e5","license":"MIT"},"@bgrusnak/solidity-icoset/contracts/structures/KPI.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

enum KPITimeStatus {
    // do not use timestamp
    Ignore,
    // kpi is always zero until the timestamp
    NotBefore,
    // kpi is always 1000 (1) after the timestamp
    AlwaysAfter
}
/*
 * KPI allows calculation of vesting value according the different options and time.
 * All KPI values are calculated in promille (1/1000) and are between 0 & 1000 (1)
 */
struct KPI {
    // the service timestamp for the KPI
    uint256 time;
    // how the timestamp should to be processed
    KPITimeStatus timeStatus;
    // current KPI value
    uint16 current;
    // KPI weight in total KPI
    uint16 weight;
}
